mapper.select:
SELECT h.hname, a.createDate, a.num, a.weight
FROM achieve_history as a, house as h
WHERE a.uid = #{userId} and h.id = a.hid ORDER BY a.id DESC ;

SELECT h.hname, c.description as commodityName, o.orderSn, o.createTime, o.pay_method ,c.price
FROM pay_orders as o,house as h, commodity as c
WHERE o.uid = #{userId} and o.hid = h.id and c.id = o.commodityId ORDER BY o.id DESC ;
